using System;using System.Collections.Generic;using System.Linq;using System.Text;using System.Threading.Tasks;using System.Windows;using System.Windows.Controls;using System.Windows.Data;using System.Windows.Documents;using System.Windows.Input;using System.Windows.Media;using System.Windows.Media.Imaging;using System.Windows.Navigation;using System.Windows.Shapes;namespace Rabbits{    public class Rabbit    {
        //  id ,name and age are the properties
        private string id;        private string name;        public int Age;        public Rabbit(string id, string name, int age)// Constructor
        {            this.id = id;            this.name = name;            this.Age = age;        }        public string info()        {            return ("(" + id + ", " + name + ", " + Age + ")");        }    }    public partial class MainWindow : Window    {        static List<Rabbit> rabbits = new List<Rabbit>();        Rabbit[] rabbit;        public MainWindow()        {            InitializeComponent();        }        private void Button100Rabbits_Click(object sender, RoutedEventArgs e)        {            rabbit = new Rabbit[100];            for (int i = 0; i < rabbit.Length; i++)            {                rabbit[i] = new Rabbit(("id " + (i + 1)), ("Rabbit " + (i)), 0);            }            for (int i = 0; i < rabbit.Length; i += 10)            {                ListBox100Rabbits.Items.Add(rabbit[i].info()); // add STRING NAME
            }        }        private void ButtonAgeRabbits100Times_Click(object sender, RoutedEventArgs e)        {            for (int i = 0; i < rabbit.Length; i++)            {                rabbit[i].Age = rabbit[i].Age + 1;            }            for (int i = 0; i < rabbit.Length; i += 10)            {                ListBoxAgeRabbits100Times.Items.Add(rabbit[i].info()); // add STRING NAME
            }        }        private void ListBoxAgeRabbits100Times_SelectionChanged(object sender, SelectionChangedEventArgs e)        {        }    }}